var days={"0":"Sunday","1":"Monday","2":"Tuesday","3":"Wednesday","4":"Thursday","5":"Friday","6":"Saturday"};$(document).ready(function(){var c=false;var e=new Firebase("https://packd.firebaseio.com/");var f=new Date();var g=f.getDay();var a=f.getHours()*100;var b=days[g];e.on("value",function(d){var h=d.child(b).child(a).val();if(h==null){$("#data").text("Either the RSF is closed, or something went wrong.")}else{$("#data").text(h)}},function(h){var d="Either the RSF is closed, or something went wrong.";$("#data").text(d)});$("#feedback_submit").click(function(){if(!c){var h=$("#feedback_form").val();var d=e.child("feedback");d.push({message:h});c=true;alert("Thanks for helping us make our data better!")}else{alert("Thanks, we got it!")}})});